mapper.select:
select mid, mname, maddr, mbirth, mphone, memail
from members
where mname = #{mname}

select mid, count(*) as count
from members
where mno = #{mno}

select count(*) as count
from members
where memail = #{arg0} and mid = #{arg1}

mapper.update:
update members set

		mid = #{id}
		mpw = #{pw}
		mbirth = #{birth}
		maddr = #{addr}
		memail = #{email}
		mphone = #{phone}


	mno = #{mno}
